data class FlavorText(val id: String, val flavor_text: String)

fun GetFlavorText(source: String = exampleStringLiteral()): String {
    val gson = Gson()
    val info: FlavorText = gson.fromJson(source, FlavorText::class.java)
    return info.flavor_text
}
